INSERT INTO `songs` VALUES (16, 'Nấu ăn cho em', NULL, 1, 241, 'Kìa mây mây ngang đầu\nKìa núi núi lô nhô\nCùng em trên con đường\nĐường bé xíu quanh co\nBăng qua những ngọn đồi\nThấy em nghiêng nghiêng cười trong đôi mắt tròn\nVà thế giới cũng nghiêng theo đôi bàn chân em\n\n[Đen]:\nMấy đứa trẻ đi lên trường, đội trên đầu là đoá mây trắng\nChân đạp lên mặt trời, môi thì cười và má hây nắng\nNhững nụ cười làm cho lòng đang bộn bề bỗng hoá ngay ngắn\nLên trên này thấy các em, anh mới thấy mình quá may mắn\nMặt trời vàng như trứng chiên\nKhi mà em đứng thẳng, núi và đồi phải đứng nghiêng\nCon đường quanh co quá, em chao lượn như cánh chim\nMai sau lớn em bơi giữa đời, em bơi khoẻ như Ánh Viên\nDù không còn là học sinh, anh thấy mình vẫn còn phải học hành\nNhư cây mọc lên từ đất, phải đủ chất thì nó mới mọc cành\nHọc cách bón thêm hạnh phúc, để cành đó mọc ra những điều lành\nVì anh nghĩ Số Phận là người luôn giám sát chứ không có điều hành\nNên anh mong các em sẽ không bao giờ bỏ cuộc\nVì anh biết tụi em nhỏ con nhưng mà dũng khí thì không thể nào nhỏ được\nYo, không thể nào mà nhỏ được!\n\n[PiaLinh]:\nKìa mây mây ngang đầu\nKìa núi núi lô nhô\nCùng em trên con đường\nĐường bé xíu quanh co\nBăng qua những ngọn đồi\nThấy em nghiêng nghiêng cười trong đôi mắt tròn\nVà thế giới cũng nghiêng theo đôi bàn chân em\n\n[Đen]:\nNấu cho các em ăn dù anh không là đầu bếp giỏi\nCũng là cách anh giúp chính mình, bớt nghĩ suy cho đầu hết mỏi\nAnh muốn thấy những vị khách nhỏ, ăn hết sạch những đồ ngon thơm\nVì anh biết những đứa trẻ này, mai này sẽ xây dựng quê hương\nNấu cho các em ăn, em lấy sức nhặt từng con chữ\nKhi có tri thức em thấy con hổ không còn hung dữ\nVà em sẽ lớn hơn cây mận ở bên vườn nhà\nLúc ấy thì mấy khe sâu chỉ là nét vẽ thêm rườm rà\nTụi mình muốn trở thành người tốt và đang phải học để làm điều đó\nTui mình nhìn các em để biết, những gì mình cần những gì mình có\nTụi mình cũng từng là trẻ thơ, cần được no cái bụng về chiều\nTui mình tập tìm cách cho đi vì biết cho đi sẽ nhận về nhiều\nVì:\nCho đi những nụ cười, những muộn phiền sẽ trôi rất xa\nCho đi niềm hạnh phúc sẽ nhận về gấp đôi gấp ba\n\n[PiaLinh]:\nTrong đôi mắt đó em thấy bầu trời, em thấy núi đồi mặt hồ trong veo\nMong chân sẽ cứng và đá luôn mềm trên mỗi con đường từng ngày em qua\nMặt trời trong trái tim hồng, vang trong lòng một tiếng gà trưa\nMong cho cây lá lên mầm, mong cho trời thuận gió hoà mưa\n\nKìa mây mây ngang đầu\nKìa núi núi lô nhô\nCùng em trên con đường\nĐường bé xíu quanh co\nBăng qua những ngọn đồi\nThấy em nghiêng nghiêng cười trong đôi mắt tròn\nVà thế giới cũng nghiêng theo đôi bàn chân em\n\n[Đen]:\nỞ dưới xuôi chúng mình gọi view này là view triệu đô, vậy thì ai mới là người dư dả?\nChúng mình làm và làm và làm, cũng chỉ để mong có ngày được thư thả\nVà khi xem lại những hình ảnh này, những áp lực chợt biến tan\nNhững nụ cười mát như nước giếng khoan\nVà những đôi mắt trong như ngọn nguồn ánh sáng\nChúng mình đều phải học cách để tốt lên qua ngày qua tháng\nLúc ban đầu thì chả ai tốt ngay được vì chả biết phải làm sao\nThế là nhìn những người tốt quanh mình xem họ làm gì và làm theo\nVà người tốt thì ngoài kia nhiều lắm!\nTôi tin vậy, tôi biết vậy!\nViệt Nam!', 'https://res.cloudinary.com/dofdtbshx/video/upload/v1768757523/songs/audio/song_1768757516978.mp3', 'https://res.cloudinary.com/dofdtbshx/image/upload/v1768757526/songs/cover/song_1768757516978_cover.jpg', '2026-01-19', 1, 'songs/audio/song_1768757516978', 'songs/cover/song_1768757516978_cover');
INSERT INTO `songs` VALUES (17, 'Những Kẻ Mộng Mơ', NULL, 9, 253, 'Biển, vẫn cứ thế trông về nơi xa\nKìa hoàng hôn rực rỡ và lấp lánh những nơi biển qua\nBờ cát vẫn nằm đợi sóng xô về, nhưng biển kia chẳng thể\nLại nhìn phía chân trời, cuộn làn sóng kia vội\nTìm hoàng hôn xa để kết đôi\nĐừng mộng mơ nữa, hỡi biển ơi\nChân trời xa lắm, chẳng có ai đợi\nPhía bên kia đại dương cũng chỉ có bờ cát nâng niu biển thôi\nĐừng mộng mơ nữa, hỡi kẻ mộng mơ\nMặt trời tận nơi góc vũ trụ bao la\nHoàng hôn đó chỉ là từng tia sáng mong manh từ nơi xa\nVà chỉ có anh bên em kế bên em khi gục ngã\n♪\nThủy triều vẫn thế đầy rồi lại vơi\nVà lại mang biển sóng ngày đến tối đi lạnh lùng vậy thôi\nBờ cát giữ biển lại mỗi khi về, nhưng biển kia chẳng thể\nLại nhìn phía chân trời, cuộn làn sóng kia vội\nTìm hoàng hôn xa để kết đôi\nĐừng mộng mơ nữa, hỡi biển ơi\nChân trời xa lắm, chẳng có ai đợi\nPhía bên kia đại dương cũng chỉ có bờ cát nâng niu biển thôi\nĐừng mộng mơ nữa, hỡi kẻ mộng mơ\nMặt trời tận nơi góc vũ trụ bao la\nHoàng hôn đó chỉ là từng tia sáng mong manh từ nơi xa\nVà chỉ có anh bên em kế bên em khi gục ngã\nÔm em thế nhưng em yêu lại đi\nĐừng lặng im như thế, biết sẽ đớn đau sao em lại đi?\nPhía bên kia đại dương cũng chỉ có bờ cát lặng im\nHoàng hôn ánh sáng xa tận ngút ngàn\nTừ góc vũ trụ lặng yên\nĐừng mộng mơ nữa, hỡi biển ơi\nChân trời xa lắm, chẳng có ai đợi\nPhía bên kia đại dương cũng chỉ có bờ cát nâng niu biển thôi\nĐừng mộng mơ nữa, hỡi kẻ mộng mơ\nMặt trời tận nơi góc vũ trụ bao la\n♪\nHoàng hôn đó chỉ là từng tia sáng mong manh từ nơi xa\nVà chỉ có anh bên em kế bên em khi gục ngã', 'https://res.cloudinary.com/dofdtbshx/video/upload/v1768757605/songs/audio/song_1768757601110.mp3', 'https://res.cloudinary.com/dofdtbshx/image/upload/v1768757608/songs/cover/song_1768757601110_cover.jpg', '2026-01-19', 1, 'songs/audio/song_1768757601110', 'songs/cover/song_1768757601110_cover');
INSERT INTO `songs` VALUES (18, 'Nỗi Đau Giữa Hòa Bình', NULL, 3, 319, 'Mọi người kể những câu chuyện xưa đã trở thành huyền thoại\nvà viết nên bao bài ca để ngàn năm hát mãi\nVề người mẹ việt nam anh hùng, đã quên mình\nGạt đi nước mắt, tiễn con lên đường\n\nNỗi đau người ở lại mấy ai hiểu được\nVì trái tim yêu đàn con và yêu đất nước\nNgười mẹ nào không xót thương con, nhớ thương con\nChờ tin chiến thắng về trong hy vọng..\n\nNgười mẹ ấy tìm con giữa tiếng reo dân tộc\nNgười vợ ấy tìm chồng giữa đám đông\nHoà bình đến rồi sao anh vẫn chưa trở về\nGiữa tiếng cười, mình mẹ rơi nước mắt\n\nĐạn bom đã ngừng bay nhưng vết thương sâu này\nVẫn âm ỉ ngày đêm làm sao nguôi\n“hoà bình đến rồi sao những đứa con của tôi\nCòn ngủ mãi giữa chiến trường thôi?”\n\nMột thời chiến tranh qua rồi mang cả con đi rồi\nChỉ có chim câu gửi về màu xanh chiếc áo\nViên đạn từ muôn hướng ghim vào trái tim mẹ\nMừng cho đất nước và đau cho mình\n\nNgười mẹ ấy tìm con giữa tiếng reo dân tộc\nNgười vợ ấy tìm chồng giữa đám đông\nHoà bình đến rồi sao anh vẫn chưa trở về\nGiữa tiếng cười, mình mẹ rơi nước mắt\n\nĐạn bom đã ngừng bay nhưng vết thương sâu này\nVẫn âm ỉ ngày đêm làm sao nguôi\n“hoà bình đến rồi sao những đứa con của tôi\nCòn ngủ mãi giữa chiến trường thôi?”\n\nĐộc lập đổi bằng bao nhiêu xương máu\nHoà bình đổi bằng bao nhiêu nỗi đau', 'https://res.cloudinary.com/dofdtbshx/video/upload/v1768757698/songs/audio/song_1768757693259.mp3', 'https://res.cloudinary.com/dofdtbshx/image/upload/v1768757700/songs/cover/song_1768757693259_cover.jpg', '2026-01-19', 1, 'songs/audio/song_1768757693259', 'songs/cover/song_1768757693259_cover');
INSERT INTO `songs` VALUES (20, 'Nơi Này Có Anh', 6, 1, 260, 'Em là ai từ đâu bước đến nơi đây dịu dàng chân phương\nEm là ai tựa như ánh nắng ban mai ngọt ngào trong sương\nNgắm em thật lâu\nCon tim anh yếu mềm\nĐắm say từ phút đó\nTừng giây trôi yêu thêm\nBao ngày qua bình minh đánh thức xua tan bộn bề nơi anh\nBao ngày qua niềm thương nỗi nhớ bay theo bầu trời trong xanh\nLiếc đôi hàng mi\nMong manh anh thẫn thờ\nMuốn hôn nhẹ mái tóc\nBờ môi em anh mơ\nCầm tay anh dựa vai anh\nKề bên anh nơi này có anh\nGió mang câu tình ca\nNgàn ánh sao vụt qua nhẹ ôm lấy em\n(Yêu em thương em con tim anh chân thành)\nCầm tay anh dựa vai anh\nKề bên anh nơi này có anh\nKhép đôi mi thật lâu\nNguyện mãi bên cạnh nhau\nYêu say đắm như ngày đầu\nMùa xuân đến bình yên\nCho anh những giấc mơ\nHạ lưu giữ ngày mưa\nNgọt ngào nên thơ\nMùa thu lá vàng rơi\nĐông sang anh nhớ em\nTình yêu bé nhỏ xin\nDành tặng riêng em\nCòn đó tiếng nói ấy bên tai\nVấn vương bao ngày qua\nÁnh mắt bối rối\nNhớ thương bao ngày qua\nYêu em anh thẫn thờ\nCon tim bâng khuâng đâu có ngờ\nChẳng bao giờ phải mong chờ\nĐợi ai trong chiều hoàng hôn mờ\nĐắm chìm hòa vào vần thơ\nNgắm nhìn khờ dại mộng mơ\nĐừng bước vội vàng rồi làm ngơ\nLạnh lùng đó làm bộ dạng thờ ơ\nNhìn anh đi em nha\nHướng nụ cười cho riêng anh nha\nĐơn giản là yêu\nCon tim anh lên tiếng thôi\nCầm tay anh dựa vai anh\nKề bên anh nơi này có anh\nGió mang câu tình ca\nNgàn ánh sao vụt qua nhẹ ôm lấy em\n(Yêu em thương em con tim anh chân thành)\nCầm tay anh dựa vai anh\nKề bên anh nơi này có anh\nKhép đôi mi thật lâu\nNguyện mãi bên cạnh nhau\nYêu say đắm như ngày đầu\nMùa xuân đến bình yên\nCho anh những giấc mơ\nHạ lưu giữ ngày mưa\nNgọt ngào nên thơ\nMùa thu lá vàng rơi\nĐông sang anh nhớ em\nTình yêu bé nhỏ xin\nDành tặng riêng em\nOh nhớ thương em\nOh nhớ thương em lắm\nAh phía sau chân trời\nCó ai băng qua lối về\nCùng em đi trên đoạn đường dài\nCầm tay anh dựa vai anh\nKề bên anh nơi này có anh\nGió mang câu tình ca\nNgàn ánh sao vụt qua nhẹ ôm lấy em\n(Yêu em thương em con tim anh chân thành)\nCầm tay anh dựa vai anh\nKề bên anh nơi này có anh\nKhép đôi mi thật lâu\nNguyện mãi bên cạnh nhau\nYêu say đắm như ngày đầu\nMùa xuân đến bình yên\nCho anh những giấc mơ\nHạ lưu giữ ngày mưa\nNgọt ngào nên thơ\nMùa thu lá vàng rơi\nĐông sang anh nhớ em\nTình yêu bé nhỏ xin\nDành tặng riêng em', 'https://res.cloudinary.com/dofdtbshx/video/upload/v1768757922/songs/audio/song_1768757919342.mp3', 'https://res.cloudinary.com/dofdtbshx/image/upload/v1768757924/songs/cover/song_1768757919342_cover.jpg', '2026-01-19', 1, 'songs/audio/song_1768757919342', 'songs/cover/song_1768757919342_cover');
INSERT INTO `songs` VALUES (21, 'Phố Xuân ', NULL, 7, 223, 'Bước cùng xuân\nTrên phố bao người xa lạ\nPhút chốc thành quen\nXôn xao niềm vui ta đón xuân\nChồi lá màu xanh xanh\nCầu chúc muôn nhà an lành\nSắc thắm ngàn hoa rộn rã tim mình\nSánh bước cùng anh\nMôi thắm xinh và xuân ngời\nThấp thoáng tình nhân\nTrao nhau nụ hôn trong tiếng cười\nLời hát nào bay xa\nMang tiếng ca về muôn nhà\nGió hát chào xuân ngàn nắng chan hoà\nMột ngày trời xuân trong xanh\nGiọt sương long lanh nụ hoa trên cành\nEm đi trong bình minh\nTrong dáng xuân yêu kiều\nThì thầm lời ai bên tai\nVà tay trong tay ngày mai ước thề\nTa đi trong tình yêu\nVui đất trời vào xuân\nBước cùng xuân\nTrên phố bao người xa lạ\nPhút chốc thành quen\nXôn xao niềm vui ta đón xuân\nChồi lá màu xanh xanh\nCầu chúc muôn nhà an lành\nSắc thắm ngàn hoa rộn rã tim mình\nSánh bước cùng anh\nMôi thắm xinh và xuân ngời\nThấp thoáng tình nhân\nTrao nhau nụ hôn trong tiếng cười\nLời hát nào bay xa\nMang tiếng ca về muôn nhà\nGió hát chào xuân ngàn nắng chan hoà\nMột ngày trời xuân trong xanh\nGiọt sương long lanh nụ hoa trên cành\nEm đi trong bình minh\nTrong dáng xuân yêu kiều\nThì thầm lời ai bên tai\nVà tay trong tay ngày mai ước thề\nTa đi trong tình yêu\nVui đất trời vào xuân\nBước cùng xuân\nTrên phố bao người xa lạ\nPhút chốc thành quen\nXôn xao niềm vui ta đón xuân\nChồi lá màu xanh xanh\nCầu chúc muôn nhà an lành\nSắc thắm ngàn hoa rộn rã tim mình\nSánh bước cùng anh\nMôi thắm xinh và xuân ngời\nThấp thoáng tình nhân\nTrao nhau nụ hôn trong tiếng cười\nLời hát nào bay xa\nMang tiếng ca về muôn nhà\nGió hát chào xuân ngàn nắng chan hoà\nMột ngày trời xuân trong xanh\nGiọt sương long lanh nụ hoa trên cành\nEm đi trong bình minh\nTrong dáng xuân yêu kiều\nThì thầm lời ai bên tai\nVà tay trong tay ngày mai ước thề\nTa đi trong tình yêu\nVui đất trời vào xuân', 'https://res.cloudinary.com/dofdtbshx/video/upload/v1768758063/songs/audio/song_1768758059099.mp3', 'https://res.cloudinary.com/dofdtbshx/image/upload/v1768758064/songs/cover/song_1768758059099_cover.jpg', '2026-01-19', 0, 'songs/audio/song_1768758059099', 'songs/cover/song_1768758059099_cover');
INSERT INTO `songs` VALUES (22, 'Tết Đong Đầy', NULL, 1, 206, 'Ngoài đường đông vui\nTràn ngập bao tiếng ca\nXuân năm nay đã đến rồi em ơi\nNgười người bên nhau\nTạm biệt năm cũ qua\nXuân yên vui về khắp bên mọi nhà\nXuân năm nay đến\nBao nhiêu câu chúc\nChúc gia đình nhà nhà luôn hạnh phúc\nHoa mai đua sắc\nCùng rạo rực đón xuân\nHy vọng một năm mới luôn bình an\nMẹ nấu bánh chưng xanh\nLàn gió khẽ lay đưa\nCùng tiếng múa lân vang\nTùng cheng tùng cheng thích mê chưa\nĐàn cháu chúc ông bà\nNhiều sức khỏe vui tươi\nNồng ấm cứ đong đầy\nCùng nhau chào xuân đến bên ta\nTết đong đầy\nXuân năm nay vui hơn năm qua\nPhúc lộc tài về với muôn mọi nhà\nTay trong tay anh và em đây vui ca\nHạnh phúc đong đầy là ngày tết sum vầy\nLá la là lá la là lá la la\nTết đong đầy\nLá la là lá la là lá la la\nTết đong đầy\nCuộc sống Sài Gòn quá tấp nập\nCon đây nhớ ba nhớ má nhớ quê\nThấy chú Tư chú Bảy lên chức\nNhìn lại thằng cháu sao mà thấy ứa ghê\nNăm nay con gửi ông bánh tét\nChứ mà mứt dừa là ông hổng ưa\nCòn thằng cu Tí biết mình nay đã lớn\nChạy theo đòi lì xì\nCòn năm ngàn cũng phải đưa\nXuân năm nay đến\nBao nhiêu câu chúc\nChúc gia đình nhà nhà luôn hạnh phúc\nHoa mai đua sắc\nCùng rạo rực đón xuân\nHy vọng một năm mới luôn bình an\nMẹ nấu bánh chưng xanh\nLàn gió khẽ lay đưa\nCùng tiếng múa lân vang\nTùng cheng tùng cheng thích mê chưa\nĐàn cháu chúc ông bà\nNhiều sức khỏe vui tươi\nNồng ấm cứ đong đầy\nCùng nhau chào xuân đến bên ta\nTết đong đầy\nXuân năm nay vui hơn năm qua\nPhúc lộc tài về với muôn mọi nhà\nTay trong tay anh và em đây vui ca\nHạnh phúc đong đầy là ngày tết sum vầy\nLá la là lá la là lá la la\nTết đong đầy\nLá la là lá la là lá la la\nTết đong đầy\nXuân năm nay đến\nBao nhiêu câu chúc\nChúc gia đình nhà nhà luôn hạnh phúc\nHoa mai đua sắc\nCùng rạo rực đón xuân\nHy vọng một năm mới luôn bình an\nMẹ nấu bánh chưng xanh\nLàn gió khẽ lay đưa\nCùng tiếng múa lân vang\nTùng cheng tùng cheng thích mê chưa\nĐàn cháu chúc ông bà\nNhiều sức khỏe vui tươi\nNồng ấm c